	private static String loadFile(String file) throws IOException {
		StringBuilder buf = new StringBuilder();
		Files.lines(Paths.get(file), StandardCharsets.UTF_8).forEach(buf :: append);
		
		return buf.toString();
	}
	
	private static void initCountry() throws Exception{
		JSONObject countryMap = JsonHelper.toJsonObject(loadFile("G:/city_en.txt") , true);
		
		System.err.println(countryMap.size());
		
		for(Map.Entry<String , Object> country : countryMap.entrySet()) {
			String name = country.getKey();
			String spell = country.getValue().toString();
			
			String ename = name.split("_")[0];
			
			mapping_Map.put(name, spell);
			
			if(!mapping_country_map.containsKey(ename)) {
				mapping_country_map.put(ename, name.split("_")[1]);
			}
		}
		//额外添加
		mapping_Map.put("BB_巴巴多斯岛", "Barbados");
		mapping_country_map.put("BB", "巴巴多斯岛");
		
		mapping_Map.put("CG_刚果民主共和国", "Congo");
		mapping_country_map.put("CG", "刚果民主共和国");
		
		mapping_Map.put("RE_留尼汪岛", "Reunion Island");
		mapping_country_map.put("RE", "留尼汪岛");
		
		mapping_Map.put("YT_马拉维", "Malawi");
		mapping_country_map.put("YT", "马拉维");
	}
	
