	private static void sssksk() throws Exception{
		JSONObject countryMap = JsonHelper.toJsonObject(loadFile("G:/city.txt") , true);
		
		System.err.println(countryMap.size());
		System.err.println(mapping_country_map.size());
		
		for(Map.Entry<String , Object> json : countryMap.entrySet()) {
			String country_ename = json.getKey();
			
			//System.err.println(country_ename);
			
			String country_name = mapping_country_map.get(country_ename);
			String country_spell = mapping_Map.get(country_ename+"_"+country_name);
			char country_initial = PinyinUtil.getFirstPinyin(country_name).charAt(0);
			
			System.err.println(country_ename+"--"+country_name+"--"+country_spell + "--" + country_initial);
			
			JSONObject provinceJson = (JSONObject)json.getValue();
			
			Country country = new Country(country_name, country_ename, country_spell, country_initial);
			countryList.add(country);
			
			//此处解析省份
			for(Map.Entry<String , Object> provinceObject : provinceJson.entrySet()) {
				String province_name = provinceObject.getKey();
				String province_ename = mapping_Map.get(country_ename+"_"+province_name);;
				String province_spell = province_ename;
				char province_initial = PinyinUtil.getFirstPinyin(province_name).charAt(0);
				
				System.err.println(province_name+"--"+province_name+"--"+province_spell + "--" + province_initial);
				
				JSONArray cityJson = (JSONArray)provinceObject.getValue();
				
				Province province = new Province(province_name, province_ename, province_spell, province_initial);
				country.provinceList.add(province);
				
				//此处解析城市
				for(int y=0;y<cityJson.size();y++) {
					String city_name = cityJson.getString(y);
					String city_ename = mapping_Map.get(country_ename+"_"+city_name);;
					String city_spell = province_ename;
					char city_initial = PinyinUtil.getFirstPinyin(city_name).charAt(0);
					
					System.err.println(city_ename+"--"+city_name+"--"+city_spell + "--" + city_initial);
					
					City city = new City(city_name, city_ename, city_spell, city_initial);
					province.cityList.add(city);
				}
			}
		}
		
		System.err.println("===============================================================");
		System.err.println("===============================================================");
		System.err.println("===============================================================");
		System.err.println("===============================================================");
		
		//此处添加中国的国家，省份，城市信息
		Country china = new Country("中国", "CN", "China", 'c');
		countryList.add(china);
		
		//此处load省份和城市
		String[] ss = new String[]{"北京","上海","香港","台湾","广东","浙江","江苏","四川","重庆","湖南","湖北","福建","广西","山东","河南","河北","辽宁","陕西","安徽","天津","江西","云南","黑龙江","山西","吉林","贵州","海南","甘肃","青海","宁夏","内蒙古","新疆","西藏","澳门"};
		JSONArray chinaProvince = JsonHelper.toJsonArray(loadFile("G:/city.json"));
		for(int i=0;i<chinaProvince.size();i++) {
			String province_name = ss[i];
			String province_spell = PinyinUtil.getFullPinyin(province_name);
			String province_ename = Tools.formatString(old_city_ename.get(province_name), province_spell);
			char province_initial = province_spell.charAt(0);
			
			System.err.println(province_name+"--"+province_name+"--"+province_spell + "--" + province_initial);
			
			JSONArray cityJson = chinaProvince.getJSONObject(i).getJSONArray(province_name);
			
			Province province = new Province(province_name, province_ename, province_spell, province_initial);
			china.provinceList.add(province);
			
			//加载城市
			for(int j=0;j<cityJson.size();j++) {
				String city_name = cityJson.getString(j);
				String city_spell = PinyinUtil.getFullPinyin(city_name);
				String city_ename = Tools.formatString(old_city_ename.get(city_name), city_spell);
				char city_initial = city_ename.charAt(0);
				
				System.err.println(city_ename+"--"+city_name+"--"+city_spell + "--" + city_initial);
				
				City city = new City(city_name, city_ename, city_spell, city_initial);
				province.cityList.add(city);
			}
		}
	}
	
	private static void p(){
		//输出sql文件
		int index = 0;
		for(int i=0;i<countryList.size();i++) {
			index++;
			
			Country country = countryList.get(i);
			int countryId = index;
			print(index, country.name, country.ename, country.spell, country.initial, 0, 0, true);
			
			for(int j=0;j<country.provinceList.size();j++) {
				index++;
				
				Province province = country.provinceList.get(j);
				int provinceId = index;
				print(index, province.name, province.ename, province.spell, province.initial, 1, countryId, true);
				
				for(int x = 0;x<province.cityList.size();x++) {
					index++;
					
					City city = province.cityList.get(x);
					print(index, city.name, city.ename, city.spell, city.initial, 2, provinceId, false);
				}
			}
		}
	}
	
	private static void print(int index , String name , String ename , String spell , char initial , int depth , int pid , boolean hasChildren) {
		//System.err.println(name+"--"+ename+"--"+spell);
		String s = "INSERT INTO t_app_area VALUES ('"+index+"', '2017-07-20 00:00:00', '2017-07-20 00:00:00', '"+name+"', '"+ename.replace("'", "\\\'")+"', '"+spell.replace("'", "\\\'")+"', '"+initial+"', '0', '"+depth+"', '"+pid+"', '"+(hasChildren?1:0)+"', '1000', '1', null, '0.0000000000', '0.0000000000', '0');";
		System.err.println(s);
	}
	
