	private static void foreachMap(){
		for(ProvinceLBS cityLBS : cityLBSList) {
			try {
				ProvinceLBS provinceLBS = provinceLBSMap.get(cityLBS.pid);
				if(provinceLBS.name.equals(cityLBS.name)) {
					cityLBS.lat = provinceLBS.lat;
					cityLBS.lng = provinceLBS.lng;
				} else {
					//调用百度地图
					BmapPoint point = BaiduUtils.getByAddress(cityLBS.name, provinceLBS.name);
					if(point == null) {
						System.err.println("城市:"+cityLBS.name+",id:"+cityLBS.id+",未匹配到经纬度");
					} else {
						cityLBS.lat = String.valueOf(point.getLat());
						cityLBS.lng = String.valueOf(point.getLng());
					}
				}
				//休息一下
				Thread.sleep(100L);
			} catch (Exception e) {
				e.printStackTrace();
			}
		}
	}
	
	private static void printCityLBS(){
		for(ProvinceLBS cityLBS : cityLBSList) {
			if(cityLBS.lat != null) {
				System.err.println("update t_app_area set lng="+cityLBS.lng+",lat="+cityLBS.lat+" where id="+cityLBS.id);
			}
		}
	}
	
